import React from "react";
import { Row, Col, Container } from "react-bootstrap";

const DatesList = ({ person }) => {
  return (
    <Container fluid>
      <Row className="justify-content-center">
        <Col sm="8">
          <div className="rectangle p-2 my-2">
            {person.length ? (
              person.map((item) => {
                return (
                  <div key={item.id} className="d-flex border-bottom mx-3 m-2">
                    <img className="img-avatar" src="p1.jpg" alt="sdsd" />
                    <div className="px-3">
                      <p className="d-inline fs-5">{item.name}</p>
                      <p className="fs-6">{item.date}</p>
                    </div>
                  </div>
                );
              })
            ) : (
              <h2 className="p-5 text-center">لا يوجد مواعيد اليوم</h2>
            )}
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default DatesList;
